import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';

const RoutinePage = () => {
  const [routines, setRoutines] = useState([]);
  const [error, setError] = useState(null);
  const [showDayForm, setShowDayForm] = useState(false);
  const [dayName, setDayName] = useState('');
  const [excerciseName, setExcerciseName] = useState('');
  const [selectedRoutineId, setSelectedRoutineId] = useState(null);
  const [showExerciseForm, setShowExerciseForm] = useState(false);
  const [selectedDayId, setSelectedDayId] = useState(null);
  const [selectedExerciseIds, setSelectedExerciseIds] = useState([]);
  const [availableExercises, setAvailableExercises] = useState([]); // Lista de ejercicios disponibles

  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    const fetchExercises = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/exercises'); // Ajusta la ruta según tu backend
        setAvailableExercises(response.data);
      } catch (error) {
        console.error('Error al obtener los ejercicios:', error);
      }
    };

    fetchRoutines();
    fetchExercises();
  }, []);

  const handleAddDayClick = (routineId) => {
    setSelectedRoutineId(routineId);
    setShowDayForm(true);
  };

  const handleDayNameChange = (e) => {
    setDayName(e.target.value);
  };

  const handleExcerciseNameChange = (e) => {
    setExcerciseName(e.target.value);
  };

  const handleDaySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/addDayToRoutine', {
        dayName,
        routineId: selectedRoutineId
      });
      setShowDayForm(false);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al crear y asociar el día a la rutina:', error);
      setError('Error al crear y asociar el día a la rutina');
    }
  };

  const handleAddExerciseClick = (dayId) => {
    setSelectedDayId(dayId);
    setShowExerciseForm(true);
  };

  const handleExerciseSelection = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setSelectedExerciseIds([...selectedExerciseIds, value]);
    } else {
      setSelectedExerciseIds(selectedExerciseIds.filter(id => id !== value));
    }
  };

  const handleExerciseSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/addExercisesToDay', {
        dayId: selectedDayId,
        exerciseNames: selectedExerciseIds
      });
      setShowExerciseForm(false);
      setSelectedExerciseIds([]);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al agregar ejercicios al día:', error);
      setError('Error al agregar ejercicios al día');
    }
  };

  return (
    <div className="routine-container">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {routines.length > 0 ? (
        routines.map((routine) => (
          <div key={routine._id} className="routine">
            <h1>{routine.name}</h1>
            {routine.days && routine.days.length > 0 ? (
              routine.days.map((day) => (
                <div className="day" key={day._id}>
                  <h2>
                    {day.name} <button onClick={() => handleAddExerciseClick(day._id)}>Agregar Ejercicios</button>
                  </h2>
                  {day.exercises && day.exercises.length > 0 ? (
                    day.exercises.map((exercise) => (
                      <div className="exercise" key={exercise._id}>
                        <p>{exercise.name}</p>
                        <p>{exercise.serie} x {exercise.repetition} {exercise.together}</p>
                        <p>{exercise.weight} Kg</p>
                      </div>
                    ))
                  ) : (
                    <p>No hay ejercicios en este día.</p>
                  )}
                </div>
              ))
            ) : (
              <p>No hay días en esta rutina.</p>
            )}
            <button className="add-day" onClick={() => handleAddDayClick(routine._id)}>+ (crear día nuevo)</button>
          </div>
        ))
      ) : (
        <p>Cargando rutinas...</p>
      )}
      {showDayForm && (
        <div className="day-form">
          <h3>Crear Nuevo Día</h3>
          <form onSubmit={handleDaySubmit}>
            <input
              type="text"
              name="name"
              value={dayName}
              onChange={handleDayNameChange}
              placeholder="Nombre del día"
              required
            />
            <button type="submit">Crear Día</button>
            <button type="button" onClick={() => setShowDayForm(false)}>Cancelar</button>
          </form>
        </div>
      )}
      {showExerciseForm && (
        <div className="exercise-form">
          <h3>Agregar Ejercicios al Día</h3>
          <form onSubmit={handleExerciseSubmit}>
            <input
              type="text"
              placeholder="Nombre del ejercicio"
              name="name"
              value={excerciseName}
              onChange={handleExcerciseNameChange}
              required
            />
            <button type="submit">Agregar Ejercicios</button>
            <button type="button" onClick={() => setShowExerciseForm(false)}>Cancelar</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default RoutinePage;
