import React, { useState } from 'react';
import './CarouselExercise.css';

const ExerciseCarousel = ({ exercises, onSelectExercise }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Verifica si el array de ejercicios existe y tiene elementos
  if (!exercises || exercises.length === 0) {
    return <p>No hay ejercicios disponibles.</p>;
  }

  const nextExercise = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % exercises.length);
  };

  const prevExercise = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + exercises.length) % exercises.length);
  };

  return (
    <div className="exercise-carousel">
      <button onClick={prevExercise}>Anterior</button>
      <div className="exercise-card">
        <h3>{exercises[currentIndex].name}</h3>
        <p>Series: {exercises[currentIndex].serie}</p>
        <p>Repeticiones: {exercises[currentIndex].repetition}</p>
        <p>Peso: {exercises[currentIndex].weight} kg</p>
        <button onClick={() => onSelectExercise(exercises[currentIndex]._id)}>Añadir a día</button>
      </div>
      <button onClick={nextExercise}>Siguiente</button>
    </div>
  );
};

export default ExerciseCarousel;
