import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';
import DaysCarousel from '../carruselDaysPage/carruselDay';
import ExercisesCarousel from '../carruselExercisePage/carruselExercise';
import { Dropdown } from 'bootstrap';

const RoutinePage = () => {
  const [routines, setRoutines] = useState([]);
  const [error, setError] = useState(null);
  const [showDayForm, setShowDayForm] = useState(false);
  const [dayName, setDayName] = useState('');
  const [selectedRoutineId, setSelectedRoutineId] = useState(null);
  const [showExerciseForm, setShowExerciseForm] = useState(false);
  const [selectedDayId, setSelectedDayId] = useState(null);
  const [isAddingExistingExercise, setIsAddingExistingExercise] = useState(false);
  const [exerciseData, setExerciseData] = useState({
    id: '',
    name: '',
    repetition: '',
    serie: '',
    combined: false,
    together: '',
    weight: ''
  });
  const [editingExercise, setEditingExercise] = useState(null);
  const [editingDay, setEditingDay] = useState(null);
  const [editingRoutine, setEditingRoutine] = useState(null);
  const [editingRoutineName, setEditingRoutineName] = useState('');
  const [editingRoutineStartDate, setEditingRoutineStartDate] = useState('');
  const [editingRoutineEndDate, setEditingRoutineEndDate] = useState('');
  const [showEditingRoutineForm, setShowEditingRoutineForm] = useState(false);
  const [showExercisesCarousel, setShowExercisesCarousel] = useState(false);
  // Estado para manejar la visibilidad del formulario
  const [showAddForm, setShowAddForm] = useState(false);
  // Estado para manejar el nombre de la rutina que se va a crear
  const [newRoutineName, setNewRoutineName] = useState('');
  const [newStartDate, setNewStartDate] = useState('');
  const [newEndDate, setNewEndDate] = useState('');
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  }, []);

  const handleAddDayClick = (routine) => {
    setSelectedRoutineId(routine._id); // Cambia a _id
    setShowDayForm(true);
  };

  const handleDaySelect = () => {
    // Aquí puedes actualizar tu lógica si es necesario
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  };

  const handleUpdateRoutineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3000/api/updateRoutine/${editingRoutine._id}`, { name: editingRoutineName, startDate: editingRoutineStartDate, endDate: editingRoutineStartDate });
      setEditingRoutine(null);
      setEditingRoutineName('');
      setShowEditingRoutineForm(false);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al actualizar la rutina:', error);
      setError('Error al actualizar la rutina');
    }
  };

  const handleDaySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/addDayToRoutine', {
        dayId: dayName,
        routineId: selectedRoutineId
      });
      setShowDayForm(false);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al crear y asociar el día a la rutina:', error);
      setError('Error al crear y asociar el día a la rutina');
    }
  };

  const handleEditDayClick = (day) => {
    setEditingDay(day);
    setDayName(day.name);
    setShowDayForm(true);
  };

  const handleUpdateDay = async (dayId) => {
    try {
      await axios.put(`http://localhost:3000/api/updateDay/${dayId}`, { name: dayName });
      setEditingDay(null);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al actualizar el día:', error);
      setError('Error al actualizar el día');
    }
  };

  const handleAddExerciseClick = (dayId) => {
    setSelectedDayId(dayId);
    const userResponse = window.confirm("¿Quieres añadir un ejercicio nuevo (Aceptar) o uno existente (Cancelar)?");
    if (userResponse) {
      setShowExerciseForm(true); // Formulario para añadir ejercicio nuevo
    } else {
      fetchExercises(); // Cargar ejercicios existentes
      setIsAddingExistingExercise(true); // Mostrar el carrusel de ejercicios existentes
    }
  };



  const handleExerciseChange = (e) => {
    const { name, value, type, checked } = e.target;
    setExerciseData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleEditExerciseClick = (exercise) => {
    setEditingExercise(exercise);
    setExerciseData({
      id: exercise._id,
      name: exercise.name,
      repetition: exercise.repetition,
      serie: exercise.serie,
      combined: exercise.combined,
      together: exercise.together,
      weight: exercise.weight
    });
    setShowExerciseForm(true);
  };

  const handleExerciseSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingExercise) {
        await axios.put(`http://localhost:3000/api/updateExercise/${exerciseData.id}`, {
          ...exerciseData,
          exerciseId: exerciseData.id
        });
      } else {
        await axios.post('http://localhost:3000/api/addExercisesToDay', {
          dayId: selectedDayId,
          exercisesId: exerciseData
        });
      }
      setShowExerciseForm(false);
      setExerciseData({
        id: '',
        name: '',
        repetition: '',
        serie: '',
        combined: false,
        together: '',
        weight: ''
      });
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al agregar o editar el ejercicio:', error);
      setError('Error al agregar o editar el ejercicio');
    }
  };

  const handleAddExerciseToDay = async (exerciseId) => {
    try {
      await axios.post('http://localhost:3000/api/addExercisesToDay', {
        dayId: selectedDayId,
        exercisesId: exerciseId,
      });
      setIsAddingExistingExercise(false);
      setShowExercisesCarousel(false); // Cierra el carrusel
      // Actualiza la lista de rutinas
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al agregar el ejercicio al día:', error);
      setError('Error al agregar el ejercicio al día');
    }
  };

  const handleDeleteExercise = async (dayId, exerciseId) => {
    try {
      await axios.delete(`http://localhost:3000/api/removeExerciseFromDay/${dayId}/${exerciseId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar el ejercicio del día:', error);
      setError('Error al eliminar el ejercicio del día');
    }
  };

  const handleDeleteDay = async (dayId, routineId) => {
    try {
      await axios.delete(`http://localhost:3000/api/removeDayFromRoutine/${routineId}/${dayId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar el día de la rutina:', error);
      setError('Error al eliminar el día de la rutina');
    }
  };

  const handleDeleteRoutine = async (routineId) => {
    try {
      await axios.delete(`http://localhost:3000/api/deleteRoutine/${routineId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar la rutina:', error);
      setError('Error al eliminar la rutina');
    }
  };

  const handleEditRoutineClick = (routine) => {
    setEditingRoutine(routine);
    setEditingRoutineName(routine.name);
    setShowEditingRoutineForm(true);
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newRoutineName) {
      setError('El nombre de la rutina es obligatorio');
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/postRoutine', {
        name: newRoutineName,
        startDate: newStartDate,
        endDate: newEndDate,
        // Aquí puedes agregar más campos como startDate, endDate, etc.
      });
      console.log('Rutina creada:', response.data);
      // Puedes actualizar tu lista de rutinas aquí si es necesario
      setShowAddForm(false); // Ocultar el formulario después de crear la rutina
      setNewRoutineName(''); // Limpiar el campo
      setNewStartDate(''); // Limpiar el campo
      setNewEndDate(''); // Limpiar el campo
    } catch (error) {
      console.error('Error al crear la rutina:', error);
      setError('Error al crear la rutina');
    }
  };

  // Función para manejar el cambio en el input
  const handleRoutineName = (e) => {
    setNewRoutineName(e.target.value);
  };

  // Función para manejar el cambio en el input
  const handleStartDate = (e) => {
    setNewStartDate(e.target.value);
  };

  // Función para manejar el cambio en el input
  const handleEndDate = (e) => {
    setNewEndDate(e.target.value);
  };

  const fetchExercises = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/exercise');
      setExercises(response.data);
    } catch (error) {
      console.error('Error al obtener los ejercicios:', error);
    }
  };

  const ExerciseCarousel = ({ exercises, onSelectExercise }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const nextExercise = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % exercises.length);
    };

    const prevExercise = () => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + exercises.length) % exercises.length);
    };


    return (
      <div class="container">
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {routines.length > 0 ? (
          routines.map((routine) => (
            <div key={routine._id} class="card-title">
              <h1 class="fw-light">
                {routine.name}
                <div class="btn-group" id='dropdown'>
                  <button class="btn bi-three-dots-vertical" type="button" data-bs-toggle="dropdown" aria-expanded="false" />
                  <ul class="dropdown-menu">
                    <li class="dropdown-item" onClick={() => handleEditRoutineClick(routine)}>
                      <button class="btn">Edit</button>
                    </li>
                    <li class="dropdown-item" onClick={() => setShowAddForm(true)}>
                      <a class="dropdown-item">Add</a>
                    </li>
                    <li class="dropdown-item" onClick={() => handleDeleteRoutine(routine._id)}>
                      <button class="btn">Delete</button>
                    </li>
                  </ul>
                </div>
              </h1>
              <div class="card">
                <DaysCarousel routineId={routine} onDaySelect={handleDaySelect} />
              </div>
              <div className="exercise-carousel">
                <button onClick={prevExercise}>Anterior</button>
                <div className="exercise-card">
                  <h3>{exercises[currentIndex].name}</h3>
                  <p>Series: {exercises[currentIndex].serie}</p>
                  <p>Repeticiones: {exercises[currentIndex].repetition}</p>
                  <p>Peso: {exercises[currentIndex].weight} kg</p>
                  <button onClick={() => onSelectExercise(exercises[currentIndex]._id)}>Añadir a día</button>
                </div>
                <button onClick={nextExercise}>Siguiente</button>
              </div>
              {routine.days && routine.days.length > 0 ? (
                routine.days.map((day) => (
                  <div class="card">
                    <div class="card-header" key={day._id}>
                      <h2 class="fw-light">
                        {day.name}
                        {/* dropdown */}
                        <div class="btn-group" id="dropdown">
                          <button class="btn btn  bi-three-dots-vertical" type="button" data-bs-toggle="dropdown" aria-expanded="false" />
                          <ul class="dropdown-menu">
                            <li class="dropdown-item" onClick={() => handleEditDayClick(day)}>
                              <a class="dropdown-item">Edit</a>
                            </li>
                            <li class="dropdown-item" onClick={handleAddDayClick}>
                              <a class="dropdown-item">Add</a>
                            </li>
                            <li class="dropdown-item" onClick={() => handleDeleteDay(day._id, routine._id)}>
                              <a class="dropdown-item">Delete</a>
                            </li>
                          </ul>
                        </div>
                      </h2>
                    </div>
                    {day.exercises && day.exercises.length > 0 ? (
                      day.exercises.map((exercise) => (
                        <div class="container text-center" key={exercise._id}>
                          <div class="row">
                            <div class="col">
                              {exercise.name}
                            </div>
                            <div class="col">
                              {exercise.serie}x{exercise.repetition} {exercise.together}
                            </div>
                            <div class="col">
                              {exercise.weight}
                            </div>
                            <div class="col">
                              <div class="btn-group" id='dropdown'>
                                <button class="btn btn  bi-three-dots-vertical" type="button" data-bs-toggle="dropdown" aria-expanded="false" />
                                <ul class="dropdown-menu">
                                  <li class="dropdown-item" onClick={() => handleEditExerciseClick(exercise)}>
                                    <a class="dropdown-item">Edit</a>
                                  </li>
                                  <li class="dropdown-item" onClick={() => handleAddExerciseClick(day._id)}>
                                    <a class="dropdown-item">Add</a>
                                  </li>
                                  <li class="dropdown-item" onClick={() => handleDeleteExercise(day._id, exercise._id)}>
                                    <a class="dropdown-item">Delete</a>
                                  </li>
                                </ul>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))
                    ) : (
                      <button class="btn btn-primary" onClick={() => handleAddExerciseClick(day._id)}>
                        <a class="dropdown-item">
                          Agregue ejercicios a este día.
                        </a>
                      </button>
                    )}

                  </div>
                ))
              ) : (
                <p>No hay días en esta rutina.</p>
              )}
            </div>
          ))
        ) : (
          <p>Cargando rutinas...</p>
        )}

        {/* Mostrar el formulario solo si showAddForm es true */}
        {showAddForm && (
          <div className="add-routine-form">
            <h3>Crear Nueva Rutina</h3>
            {error && <p style={{ color: 'red' }}>{error}</p>} {/* Mostrar el error si existe */}
            <form onSubmit={handleSubmit}>
              <div>
                <label htmlFor="routineName">Nombre de la rutina</label>
                <input
                  type="text"
                  id="routineName"
                  label="nombre de la rutina"
                  value={newRoutineName}
                  onChange={handleRoutineName}
                  placeholder="Nombre de la rutina"
                  required
                />
                <input
                  type="date"
                  id="startDate"
                  label="Start date"
                  value={newStartDate}
                  onChange={handleStartDate}
                  placeholder="Start dat4"
                  required
                />
                <input
                  type="date"
                  id="routineName"
                  label="End date"
                  value={newEndDate}
                  onChange={handleEndDate}
                  placeholder="End date"
                  required
                />
              </div>
              <div>
                <button type="submit">Crear Rutina</button>
                <button type="button" onClick={() => setShowAddForm(false)}>
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        )}



        {/* Formulario para agregar días */}
        {showDayForm && (
          <form onSubmit={editingDay ? () => handleUpdateDay(editingDay._id) : handleDaySubmit}>
            <input
              type="text"
              placeholder="Nombre del día"
              value={dayName}
              onChange={(e) => setDayName(e.target.value)}
              required
            />
            <button type="submit">{editingDay ? 'Actualizar' : 'Agregar'} Día</button>
            <button type="button" onClick={() => setShowDayForm(false)}>Cancelar</button>
          </form>
        )}
        {/* Formulario para agregar/editar ejercicios */}
        {showExerciseForm && (
          <form onSubmit={handleExerciseSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Nombre del ejercicio"
              value={exerciseData.name}
              onChange={handleExerciseChange}
              required
            />
            <input
              type="number"
              name="serie"
              placeholder="Series"
              value={exerciseData.serie}
              onChange={handleExerciseChange}
              required
            />
            <input
              type="number"
              name="repetition"
              placeholder="Repeticiones"
              value={exerciseData.repetition}
              onChange={handleExerciseChange}
              required
            />
            <input
              type="number"
              name="weight"
              placeholder="Peso (Kg)"
              value={exerciseData.weight}
              onChange={handleExerciseChange}
              required
            />
            <button type="submit">{editingExercise ? 'Actualizar' : 'Agregar'} Ejercicio</button>
            <button type="button" onClick={() => setShowExerciseForm(false)}>Cancelar</button>
          </form>
        )}

        {showEditingRoutineForm && (
          <div className="edit-routine-form">
            <h3>Editar Rutina</h3>
            <form onSubmit={handleUpdateRoutineSubmit}>
              <input
                type="text"
                value={editingRoutineName}
                onChange={(e) => setEditingRoutineName(e.target.value)}
                placeholder="Nombre de la rutina"
                required
              />
              <input
                type="date"
                id="startDate"
                label="Start date"
                value={editingRoutineStartDate}
                onChange={(e) => setEditingRoutineStartDate(e.target.value)}
                placeholder="Start dat4"
                required
              />
              <input
                type="date"
                id="routineName"
                label="End date"
                value={editingRoutineEndDate}
                onChange={(e) => setEditingRoutineEndDate(e.target.value)}
                placeholder="End date"
                required
              />
              <button type="submit">Actualizar Rutina</button>
              <button type="button" onClick={() => { setShowEditingRoutineForm(false); setEditingRoutineName(''); }}>Cancelar</button>
            </form>
          </div>
        )}

        {isAddingExistingExercise && (
          <ExerciseCarousel
            exercises={exercises}
            onSelectExercise={handleAddExerciseToDay}
          />

        )}

        {/* 
        {isAddingExistingExercise && (
          <div className="modal">
            <h3>Selecciona un ejercicio existente</h3>
            <ExercisesCarousel
              onExerciseSelect={(exerciseId) => {
                handleAddExerciseToDay(exerciseId);
              }}
              // Agrego estos valores para que no me de warnings  
              onClose={() => setIsAddingExistingExercise(false)} // Cierra el modal
            />
          </div>
        )} */}

        {/* Renderizado del pop-up */}
        {showExercisesCarousel && (
          <div className="popup">
            <ExercisesCarousel
              onExerciseSelect={(exerciseId) => {
                // Aquí debes manejar la lógica para agregar el ejercicio a la rutina
                // Puedes llamar a tu función para agregar el ejercicio a la rutina aquí
                handleAddExerciseToDay(exerciseId);
              }}
              onClose={() => setIsAddingExistingExercise(false)} // Cierra el modal
            />
          </div>
        )}
      </div>
    );
  };
};
  export default RoutinePage;
