import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';

const RoutinePage = () => {
  const [routines, setRoutines] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs); // Ajusta si necesitas acceder a otra propiedad
        console.log(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  }, []);

  return (
    <div className="routine-container">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {routines.length > 0 ? (
        routines.map((routine) => (
          <div key={routine._id} className="routine">
            <h1>{routine.name}</h1>
            {routine.days && routine.days.length > 0 ? (
              routine.days.map((day) => (
                <div className="day" key={day._id}>
                  <h2>
                    {day.name} <button>+ (agregar día antes o después)</button>
                  </h2>
                  {day.exercises && day.exercises.length > 0 ? (
                    day.exercises.map((exercise) => (
                      <div className="exercise" key={exercise._id}>
                        <p>{exercise.name}</p>
                        <p>{exercise.serie}<a>x</a>{exercise.repetition} {exercise.combined}</p>
                        <p>{exercise.together}</p>
                        <p>{exercise.weight}</p>                        
                        <button>+ (agregar ejercicio antes o después)</button>
                      </div>
                    ))
                  ) : (
                    <p>No hay ejercicios en este día.</p>
                  )}
                </div>
              ))
            ) : (
              <p>No hay días en esta rutina.</p>
            )}
            <button className="add-day">+ (para crear día nuevo)</button>
          </div>
        ))
      ) : (
        <p>Cargando rutinas...</p>
      )}
    </div>
  );
};

export default RoutinePage;
