import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';

const RoutinePage = () => {
  const [routines, setRoutines] = useState([]);
  const [error, setError] = useState(null);
  const [showDayForm, setShowDayForm] = useState(false); // Estado para mostrar el formulario
  const [dayName, setDayName] = useState(''); // Estado para almacenar el nombre del día
  const [selectedRoutineId, setSelectedRoutineId] = useState(null); // Almacena la rutina seleccionada

  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  }, []);

  const handleAddDayClick = (routineId) => {
    setSelectedRoutineId(routineId); // Guardamos la rutina en la que se añadirá el día
    setShowDayForm(true);
  };

  const handleDaySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/addDayToRoutine', {
        name: dayName,          // Envía el nombre del día
        routineId: selectedRoutineId
      });
      setShowDayForm(false);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al crear y asociar el día a la rutina:', error);
      setError('Error al crear y asociar el día a la rutina');
    }
  };

  return (
    <div className="routine-container">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {routines.length > 0 ? (
        routines.map((routine) => (
          <div key={routine._id} className="routine">
            <h1>{routine.name}</h1>
            {routine.days && routine.days.length > 0 ? (
              routine.days.map((day) => (
                <div className="day" key={day._id}>
                  <h2>{day.name} <button>+</button></h2>
                </div>
              ))
            ) : (
              <p>No hay días en esta rutina.</p>
            )}
            <button className="add-day" onClick={() => handleAddDayClick(routine._id)}>
              + (crear día nuevo)
            </button>
          </div>
        ))
      ) : (
        <p>Cargando rutinas...</p>
      )}

      {showDayForm && (
        <form onSubmit={handleDaySubmit} className="day-form">
          <input
            type="text"
            placeholder="Nombre del día"
            value={dayName}
            onChange={(e) => setDayName(e.target.value)}
            required
          />
          <button type="submit">Crear Día</button>
        </form>
      )}
    </div>
  );
};

export default RoutinePage;
