import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CarouselExercise.css';

const ExerciseCarousel = ({ onSelectExercise }) => {
  const [currentIndex, setCurrentIndex] = useState(0);    
  const [exercises, setExercises] = useState([]); // Estado para almacenar los ejercicios
  const [error, setError] = useState(null); // Estado para manejar errores

  // Cargar ejercicios al montar el componente
  useEffect(() => {
    const fetchExercises = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/exercise');
        setExercises(response.data.docs); // Asigna los ejercicios al estado
      } catch (error) {
        console.error('Error al obtener los ejercicios:', error);
        setError('No se pudieron cargar los ejercicios');
      }
    };
    fetchExercises(); // Llama a la función para cargar ejercicios
  }, []); // Este hook solo se ejecuta una vez al montar el componente

  // Verifica si el array de ejercicios está vacío y muestra el mensaje correspondiente
  if (exercises.length === 0) {
    return <p>No hay ejercicios disponibles.</p>;
  }

  const nextExercise = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % exercises.length);
  };

  const prevExercise = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + exercises.length) % exercises.length);
  };

  const currentExercise = exercises[currentIndex] || {};

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Carrousel */}
      <div className="exercise-carousel">
        <button className="btn btn-outline-primary" onClick={prevExercise}>Anterior</button>

        <div className="exercise-card">
          <h3>{currentExercise.name || 'Ejercicio no disponible'}</h3>
          <p>Series: {currentExercise.serie || 'No disponible'}</p>
          <p>Repeticiones: {currentExercise.repetition || 'No disponible'}</p>
          <p>Peso: {currentExercise.weight || 'No disponible'} kg</p>
          <button className="btn btn-outline-primary" onClick={() => onSelectExercise(currentExercise._id)}>Añadir a día</button>
        </div>

        <button className="btn btn-outline-primary" onClick={nextExercise}>Siguiente</button>
      </div>
    </div>
  );
};

export default ExerciseCarousel;
