import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick'; // Importa Slider de react-slick
import './carouselRoutine.css';

const RoutineCarousel = ({ onSelectRoutine }) => {
  const [routines, setRoutine] = useState([]); // Estado para almacenar los ejercicios
  const [error, setError] = useState(null); // Estado para manejar errores

  // Cargar rutinas al montar el componente
  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routines');
        setRoutine(response.data.docs); // Asigna los ejercicios al estado
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('No se pudieron cargar las rutinas');
      }
    };
    fetchRoutines(); // Llama a la función para cargar ejercicios
  }, []); // Este hook solo se ejecuta una vez al montar el componente

  // Verifica si el array de ejercicios está vacío y muestra el mensaje correspondiente
  if (routines.length === 0) {
    return <p>No hay rutinas disponibles.</p>;
  }

  // Configuración del slider
  const settings = {
    dots: true, // Muestra puntos de navegación
    infinite: true, // Hacer que el carrousel sea infinito
    speed: 500, // Velocidad de transición
    slidesToShow: 1, // Mostrar 1 ejercicio a la vez
    slidesToScroll: 1, // Desplazar 1 ejercicio por vez
  };

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Carrousel con Slider */}
      <Slider {...settings}>
        {routines.map((routine) => (
          <div key={routine._id} className="routine-card">
            <h3>{routine.name || 'Rutina no disponible'}</h3>
            <button className="btn btn-outline-primary" onClick={() => onSelectRoutine(routine._id)}>Añadir rutina</button>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default RoutineCarousel;
