// src/RoutinePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';

const RoutinePage = () => {
  const [routine, setRoutine] = useState(null);
  const [days, setDays] = useState([]);
  const [error, setError] = useState(null);
  
  const routineId = '671d2b625103e0e523a76918'; // Cambia esto por el ID real

  useEffect(() => {
    const fetchRoutine = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/getRoutine/${routineId}`);
        setRoutine(response.data);
      } catch (error) {
        console.error('Error al obtener la rutina:', error);
        setError('Error al obtener la rutina');
      }
    };

    fetchRoutine();
  }, [routineId]);

  useEffect(() => {
    const fetchDays = async () => {
      if (routine) {
        try {
          const response = await axios.get(`http://localhost:3000/api/getDay/${routineId}`); // Asegúrate de que esta URL sea correcta
          setDays(response.data); // Asumiendo que la respuesta es un array de días
        } catch (error) {
          console.error('Error al obtener los días:', error);
          setError('Error al obtener los días');
        }
      }
    };

    fetchDays();
  }, [routine]); // Cambiar days por routine

  const fetchExercises = async (dayId) => {
    try {
      const response = await axios.get(`http://localhost:3000/api/getExercise/${dayId}`); // Asegúrate de que esta URL sea correcta
      return response.data; // Retorna la lista de ejercicios
    } catch (error) {
      console.error('Error al obtener los ejercicios:', error);
      setError('Error al obtener los ejercicios');
      return [];
    }
  };

  return (
    <div className="routine-container">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {routine ? (
        <>
          <h1>{routine.name}</h1>
          {days.length > 0 ? (
            days.map((day, index) => (
              <div className="day" key={index}>
                <h2>
                  {day.name} <button>+ (agregar día antes o después)</button>
                </h2>
                {day.exercises && day.exercises.length > 0 ? (
                  day.exercises.map((exercise, exerciseIndex) => (
                    <div className="exercise" key={exerciseIndex}>
                      <p>{exercise.name}</p>
                      <button>+ (agregar ejercicio antes o después)</button>
                    </div>
                  ))
                ) : (
                  <p>No hay ejercicios en este día.</p>
                )}
              </div>
            ))
          ) : (
            <p>No hay días en esta rutina.</p>
          )}
          <button className="add-day">+ (para crear día nuevo)</button>
        </>
      ) : (
        <p>Cargando rutina...</p>
      )}
    </div>
  );
};

export default RoutinePage;
