const index = (req, res, next) => {
  const page = req.query.page || 1;
  const limit = req.query.limit || 10;

  Routine.paginate({}, { page, limit, populate: 'days' })
      .then(response => {
          res.json(response); // Responde directamente con los datos, incluyendo los días completos
      })
      .catch(error => {
          console.error('Error en la consulta:', error);
          res.status(500).json({ message: 'Error has occurred: ' + error.message });
      });
};
