import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import './Carousel.css'; // Asegúrate de incluir estilos para el carrusel

const DaysCarousel = ({ routineId, onDaySelect }) => {
  const [days, setDays] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDays = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/day');
        console.log('Días obtenidos:', response.data.docs);
        setDays(response.data.docs || []); // Asegúrate de que estás accediendo a 'docs'
      } catch (error) {
        console.error('Error al obtener los días:', error);
        setError('Error al obtener los días');
      }
    };
    fetchDays();
  }, []);

  const handleDayClick = async (dayId) => {
    try {
      await axios.post(`http://localhost:3000/api/addExistingDayToRoutine/${routineId}/${dayId}`);
      onDaySelect(); // Callback para actualizar el estado de la rutina
    } catch (error) {
      console.error('Error al agregar el día a la rutina:', error);
      setError('Error al agregar el día a la rutina');
    }
  };
  

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <Slider {...settings}>
        {days.length > 0 ? (
          days.map((day) => (
            <div key={day._id} className="day-slide">
              <h3>{day.name}</h3>
              <button onClick={() => handleDayClick(day._id)}>Agregar a la rutina</button>
            </div>
          ))
        ) : (
          <p>No hay días disponibles.</p>
        )}
      </Slider>
    </div>
  );
};

export default DaysCarousel;
