import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DaysCarousel = ({ onAddDay }) => {
  const [days, setDays] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDays = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/day');
        setDays(response.data.docs || []); // Asegúrate de acceder a 'docs'
      } catch (error) {
        console.error('Error al obtener los días:', error);
        setError('Error al obtener los días');
      }
    };

    fetchDays();
  }, []);

  return (
    <div className="carousel">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {days.length > 0 ? (
        days.map(day => (
          <div key={day._id} className="day-card" onClick={() => onAddDay(day)}>
            <h3>{day.name}</h3>
          </div>
        ))
      ) : (
        <p>No hay días disponibles.</p>
      )}
    </div>
  );
};

export default DaysCarousel;
