import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Routine.css';
import DaysCarousel from '../carruselDaysPage/carruselDay';
import ExercisesCarousel from '../carruselExercisePage/carruselExercise';
import { Dropdown } from 'bootstrap';

const RoutinePage = () => {
  const [routines, setRoutines] = useState([]);
  const [error, setError] = useState(null);
  const [showDayForm, setShowDayForm] = useState(false);
  const [dayName, setDayName] = useState('');
  const [selectedRoutineId, setSelectedRoutineId] = useState(null);
  const [showExerciseForm, setShowExerciseForm] = useState(false);
  const [selectedDayId, setSelectedDayId] = useState(null);
  const [isAddingExistingExercise, setIsAddingExistingExercise] = useState(false);
  const [exerciseData, setExerciseData] = useState({
    id: '',
    name: '',
    repetition: '',
    serie: '',
    combined: false,
    together: '',
    weight: ''
  });
  const [editingExercise, setEditingExercise] = useState(null);
  const [editingDay, setEditingDay] = useState(null);
  const [editingRoutine, setEditingRoutine] = useState(null);
  const [editingRoutineName, setEditingRoutineName] = useState('');
  const [showEditingRoutineForm, setShowEditingRoutineForm] = useState(false);
  const [showExercisesCarousel, setShowExercisesCarousel] = useState(false);

  useEffect(() => {
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  }, []);

  const handleAddDayClick = (routine) => {
    setSelectedRoutineId(routine._id); // Cambia a _id
    setShowDayForm(true);
  };

  const handleDaySelect = () => {
    // Aquí puedes actualizar tu lógica si es necesario
    const fetchRoutines = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/routine');
        setRoutines(response.data.docs);
      } catch (error) {
        console.error('Error al obtener las rutinas:', error);
        setError('Error al obtener las rutinas');
      }
    };

    fetchRoutines();
  };

  const handleUpdateRoutineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3000/api/updateRoutine/${editingRoutine._id}`, { name: editingRoutineName });
      setEditingRoutine(null);
      setEditingRoutineName('');
      setShowEditingRoutineForm(false);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al actualizar la rutina:', error);
      setError('Error al actualizar la rutina');
    }
  };

  const handleDaySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/addDayToRoutine', {
        dayId: dayName,
        routineId: selectedRoutineId
      });
      setShowDayForm(false);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al crear y asociar el día a la rutina:', error);
      setError('Error al crear y asociar el día a la rutina');
    }
  };

  const handleEditDayClick = (day) => {
    setEditingDay(day);
    setDayName(day.name);
    setShowDayForm(true);
  };

  const handleUpdateDay = async (dayId) => {
    try {
      await axios.put(`http://localhost:3000/api/updateDay/${dayId}`, { name: dayName });
      setEditingDay(null);
      setDayName('');
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al actualizar el día:', error);
      setError('Error al actualizar el día');
    }
  };

  const handleAddExerciseClick = (dayId) => {
    setSelectedDayId(dayId);
    const userResponse = window.confirm("¿Quieres añadir un ejercicio nuevo (Aceptar) o uno existente (Cancelar)?");
    if (userResponse) {
      setShowExerciseForm(true); // Añadir ejercicio nuevo
    } else {
      setIsAddingExistingExercise(true); // Añadir ejercicio existente
    }
  };


  const handleExerciseChange = (e) => {
    const { name, value, type, checked } = e.target;
    setExerciseData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleEditExerciseClick = (exercise) => {
    setEditingExercise(exercise);
    setExerciseData({
      id: exercise._id,
      name: exercise.name,
      repetition: exercise.repetition,
      serie: exercise.serie,
      combined: exercise.combined,
      together: exercise.together,
      weight: exercise.weight
    });
    setShowExerciseForm(true);
  };

  const handleExerciseSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingExercise) {
        await axios.put(`http://localhost:3000/api/updateExercise/${exerciseData.id}`, {
          ...exerciseData,
          exerciseId: exerciseData.id
        });
      } else {
        await axios.post('http://localhost:3000/api/addExercisesToDay', {
          dayId: selectedDayId,
          exercisesId: exerciseData
        });
      }
      setShowExerciseForm(false);
      setExerciseData({
        id: '',
        name: '',
        repetition: '',
        serie: '',
        combined: false,
        together: '',
        weight: ''
      });
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al agregar o editar el ejercicio:', error);
      setError('Error al agregar o editar el ejercicio');
    }
  };

  const handleAddExerciseToDay = async (exerciseId) => {
    try {
      await axios.post('http://localhost:3000/api/addExercisesToDay', {
        dayId: selectedDayId,
        exercisesId: exerciseId,
      });
      setShowExercisesCarousel(false); // Cierra el carrusel
      // Actualiza la lista de rutinas
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al agregar el ejercicio al día:', error);
      setError('Error al agregar el ejercicio al día');
    }
  };

  const handleDeleteExercise = async (dayId, exerciseId) => {
    try {
      await axios.delete(`http://localhost:3000/api/removeExerciseFromDay/${dayId}/${exerciseId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar el ejercicio del día:', error);
      setError('Error al eliminar el ejercicio del día');
    }
  };

  const handleDeleteDay = async (dayId, routineId) => {
    try {
      await axios.delete(`http://localhost:3000/api/removeDayFromRoutine/${routineId}/${dayId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar el día de la rutina:', error);
      setError('Error al eliminar el día de la rutina');
    }
  };

  const handleDeleteRoutine = async (routineId) => {
    try {
      await axios.delete(`http://localhost:3000/api/deleteRoutine/${routineId}`);
      const response = await axios.get('http://localhost:3000/api/routine');
      setRoutines(response.data.docs);
    } catch (error) {
      console.error('Error al eliminar la rutina:', error);
      setError('Error al eliminar la rutina');
    }
  };

  const handleEditRoutineClick = (routine) => {
    setEditingRoutine(routine);
    setEditingRoutineName(routine.name);
    setShowEditingRoutineForm(true);
  };


  return (
    <div class="container">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {routines.length > 0 ? (
        routines.map((routine) => (
          <div key={routine._id} class="card-title">
            <h1 class="fw-light">
              {routine.name}
              <button class="btn btn-outline-primary bi-x-circle-fill" onClick={() => handleDeleteRoutine(routine._id)}></button>
              <button class="btn btn-outline-primary bi bi-pencil" onClick={() => handleEditRoutineClick(routine)}></button>
            </h1>
            <div class="card">
              <DaysCarousel routineId={routine} onDaySelect={handleDaySelect} />
            </div>
            {routine.days && routine.days.length > 0 ? (
              routine.days.map((day) => (
                <div class="card">
                  <div class="card-header" key={day._id}>
                    <h2 class="fw-light">
                      {day.name}

                      {/* dropdown */}
                      <div class="btn-group">
                        <button class="btn btn-primary btn-sm bi-list" type="button" data-bs-toggle="dropdown" aria-expanded="false" />
                        <ul class="dropdown-menu">
                          <li class="dropdown-item">
                            <button class="btn btn-outline-primary" onClick={() => handleEditDayClick(day)}>Edit</button>
                          </li>
                          <li class="dropdown-item">
                            <button class="btn btn-outline-primary" onClick={() => handleAddExerciseClick(day._id)}> Add </button>
                          </li>
                          <li class="dropdown-item">
                            <button class="btn btn-outline-primary" onClick={() => handleDeleteDay(day._id, routine._id)}> Delete </button>
                          </li>
                        </ul>
                      </div>

                    </h2>
                  </div>
                  {day.exercises && day.exercises.length > 0 ? (
                    day.exercises.map((exercise) => (
                      <div class="container text-center" key={exercise._id}>
                        <div class="row">
                          <div class="col">
                            {exercise.name}
                          </div>
                          <div class="col">
                            {exercise.serie}x{exercise.repetition} {exercise.together}
                          </div>
                          <div class="col">
                            {exercise.weight}
                          </div>
                          <div class="col">
                            <button class="btn btn-outline-primary bi bi-pencil" onClick={() => handleEditExerciseClick(exercise)}></button>
                            <button class="btn btn-outline-primary bi-x-circle-fill" onClick={() => handleDeleteExercise(day._id, exercise._id)}></button>
                          </div>
                        </div>

                        {/* <li class="list-group-item">{exercise.name}</li>
                          <li class="list-group-item">{exercise.serie}x{exercise.repetition} {exercise.together}</li>
                          <li class="list-group-item">{exercise.weight} Kg</li> */}
                      </div>
                    ))
                  ) : (
                    <p>No hay ejercicios en este día.</p>
                  )}

                </div>
              ))
            ) : (
              <p>No hay días en esta rutina.</p>
            )}
            <button className="add-day" onClick={() => handleAddDayClick(routine)}><i class="bi bi-plus-circle"></i></button>
          </div>
        ))
      ) : (
        <p>Cargando rutinas...</p>
      )}
      {/* Formulario para agregar días */}
      {showDayForm && (
        <form onSubmit={editingDay ? () => handleUpdateDay(editingDay._id) : handleDaySubmit}>
          <input
            type="text"
            placeholder="Nombre del día"
            value={dayName}
            onChange={(e) => setDayName(e.target.value)}
            required
          />
          <button type="submit">{editingDay ? 'Actualizar' : 'Agregar'} Día</button>
          <button type="button" onClick={() => setShowDayForm(false)}>Cancelar</button>
        </form>
      )}
      {/* Formulario para agregar/editar ejercicios */}
      {showExerciseForm && (
        <form onSubmit={handleExerciseSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Nombre del ejercicio"
            value={exerciseData.name}
            onChange={handleExerciseChange}
            required
          />
          <input
            type="number"
            name="serie"
            placeholder="Series"
            value={exerciseData.serie}
            onChange={handleExerciseChange}
            required
          />
          <input
            type="number"
            name="repetition"
            placeholder="Repeticiones"
            value={exerciseData.repetition}
            onChange={handleExerciseChange}
            required
          />
          <input
            type="number"
            name="weight"
            placeholder="Peso (Kg)"
            value={exerciseData.weight}
            onChange={handleExerciseChange}
            required
          />
          <button type="submit">{editingExercise ? 'Actualizar' : 'Agregar'} Ejercicio</button>
          <button type="button" onClick={() => setShowExerciseForm(false)}>Cancelar</button>
        </form>
      )}

      {showEditingRoutineForm && (
        <div className="edit-routine-form">
          <h3>Editar Rutina</h3>
          <form onSubmit={handleUpdateRoutineSubmit}>
            <input
              type="text"
              value={editingRoutineName}
              onChange={(e) => setEditingRoutineName(e.target.value)}
              placeholder="Nombre de la rutina"
              required
            />
            <button type="submit">Actualizar Rutina</button>
            <button type="button" onClick={() => { setShowEditingRoutineForm(false); setEditingRoutineName(''); }}>Cancelar</button>
          </form>
        </div>
      )}

      {isAddingExistingExercise && (
        <div className="modal">
          <h3>Selecciona un ejercicio existente</h3>
          <ExercisesCarousel
            onExerciseSelect={(exerciseId) => {
              handleAddExerciseToDay(exerciseId);
            }}
            // Agrego estos valores para que no me de warnings  
            onClose={() => setIsAddingExistingExercise(false)} // Cierra el modal
          />
        </div>
      )}

      {/* Renderizado del pop-up */}
      {showExercisesCarousel && (
        <div className="popup">
          <ExercisesCarousel
            onExerciseSelect={(exerciseId) => {
              // Aquí debes manejar la lógica para agregar el ejercicio a la rutina
              // Puedes llamar a tu función para agregar el ejercicio a la rutina aquí
              handleAddExerciseToDay(exerciseId);
            }}
            onClose={() => setIsAddingExistingExercise(false)} // Cierra el modal
          />
        </div>
      )}
    </div>
  );
};

export default RoutinePage;
