import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import './CarouselExercise.css';

const ExercisesCarousel = ({ onExerciseSelect, onClose }) => {
  const [exercises, setExercises] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchExercises = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/exercise'); // Cambia la ruta si es necesario
        setExercises(response.data.docs);
      } catch (error) {
        console.error('Error al obtener los ejercicios:', error);
        setError('Error al obtener los ejercicios');
      }
    };
    fetchExercises();
  }, []);

  const handleExerciseClick = (exerciseId) => {
    onExerciseSelect(exerciseId); // Llama al callback con el ID del ejercicio
    onClose(); // Cierra el carrusel
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <Slider {...settings}>
        {exercises.map((exercise) => (
          <div key={exercise._id} className="exercise-slide">
            <h3>{exercise.name}</h3>
            <button onClick={() => handleExerciseClick(exercise._id)}>Agregar a la rutina</button>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ExercisesCarousel;
